using DomainElement = Moba.Domain.SignalBoxPlanElement;
using DomainPlan = Moba.Domain.SignalBoxPlan;
using DomainSymbol = Moba.Domain.SignalBoxSymbol;

/// <summary>
/// Electronic Signal Box (Elektronisches Stellwerk - ESTW) page.
/// Features: Colorful track diagram with German railway signals (Ks, H/V, Hl),
/// Drag&Drop from toolbox, mouse wheel rotation, LED effects, hover states.
/// 
/// Signal systems supported:
/// - Ks (Kombinationssignal) - Modern unified system
/// - H/V (Haupt-/Vorsignal) - Classic light signals
/// - Formsignale (semaphore) - Historical signals
/// </summary>
public sealed class SignalBoxPage : Page
{
    // ESTW Color Scheme (based on real electronic interlocking displays)
    private static readonly Color ColorBackground = Color.FromArgb(255, 20, 40, 60);     // Dark blue background
    private static readonly Color ColorTrackFree = Color.FromArgb(255, 100, 100, 100);   // Gray - free
    private static readonly Color ColorTrackOccupied = Color.FromArgb(255, 255, 50, 50); // Bright red - occupied
    private static readonly Color ColorRouteSet = Color.FromArgb(255, 50, 255, 50);      // Bright green - route set
    private static readonly Color ColorRouteClearing = Color.FromArgb(255, 255, 255, 50);// Yellow - clearing
    private static readonly Color ColorBlocked = Color.FromArgb(255, 100, 150, 255);     // Blue - blocked
    private static readonly Color ColorSignalRed = Color.FromArgb(255, 255, 0, 0);       // Signal Hp0
    private static readonly Color ColorSignalGreen = Color.FromArgb(255, 0, 255, 0);     // Signal Hp1
    private static readonly Color ColorSignalYellow = Color.FromArgb(255, 255, 200, 0);  // Signal Vr

    private const string DragDataNewElement = "NewElement:";
    private const string DragDataMoveElement = "MoveElement:";

    // Grid settings
    private const int GridCellSize = 60;
    private const int GridColumns = 20;
    private const int GridRows = 12;

    private readonly MainWindowViewModel _viewModel;
    private readonly IZ21 _z21;
    private readonly ObservableCollection<string> _messageLog = [];
    private readonly List<SignalBoxElement> _elements = [];
    private readonly Dictionary<Guid, FrameworkElement> _elementVisuals = [];

    private Canvas _trackCanvas = null!;
    private ListView _messageListView = null!;
    private TextBlock _statusTextBlock = null!;
    private StackPanel _toolbox = null!;

    // Properties Panel
    private StackPanel _propertiesPanel = null!;
    private SignalBoxElement? _selectedElement;
    private FrameworkElement? _selectedVisual;

    public SignalBoxPage(MainWindowViewModel viewModel, IZ21 z21)
    {
        _viewModel = viewModel;
        _z21 = z21;

        // Subscribe to Solution lifecycle events for persistence
        _viewModel.SolutionSaving += OnSolutionSaving;
        _viewModel.SolutionLoaded += OnSolutionLoaded;

        Content = BuildLayout();
        Loaded += OnLoaded;
        Unloaded += OnUnloaded;
    }

    private void OnSolutionSaving(object? sender, EventArgs e)
    {
        SaveToDomainPlan();
    }

    private void OnSolutionLoaded(object? sender, EventArgs e)
    {
        // Load data when a new solution is loaded
        var project = _viewModel.Solution?.Projects?.FirstOrDefault();
        if (project?.SignalBoxPlan != null)
        {
            LoadFromDomainPlan(project.SignalBoxPlan);
        }
        else
        {
            // Clear current state for new/empty solution
            _elements.Clear();
            _elementVisuals.Clear();
            if (_trackCanvas != null)
            {
                _trackCanvas.Children.Clear();
                DrawGrid();
            }
        }
    }

    private void OnLoaded(object sender, RoutedEventArgs e)
    {
        // Re-subscribe to Z21 feedback when page is loaded
        _z21.Received += Z21_FeedbackReceived;

        // Load from project if available (only if not already loaded)
        if (_elements.Count == 0)
        {
            var project = _viewModel.Solution?.Projects?.FirstOrDefault();
            if (project?.SignalBoxPlan != null && project.SignalBoxPlan.Elements.Count > 0)
            {
                LoadFromDomainPlan(project.SignalBoxPlan);
                LogMessage("INFO", $"Loaded {_elements.Count} elements from project");
            }
            else
            {
                LogMessage("INFO", "Signal Box (ESTW) initialized - Drag elements from toolbox");
            }
        }
    }

    private void OnUnloaded(object sender, RoutedEventArgs e)
    {
        // Only unsubscribe from Z21 feedback when navigating away
        // Keep SolutionSaving subscription active (Singleton page)
        _z21.Received -= Z21_FeedbackReceived;
    }

    private void SaveToDomainPlan()
    {
        var project = _viewModel.Solution?.Projects?.FirstOrDefault();
        if (project == null) return;

        var plan = new DomainPlan
        {
            Name = "Stellwerk",
            GridWidth = GridColumns,
            GridHeight = GridRows,
            CellSize = GridCellSize
        };

        foreach (var element in _elements)
        {
            plan.Elements.Add(new DomainElement
            {
                Id = element.Id,
                Symbol = MapToSymbol(element.Type),
                X = element.GridX,
                Y = element.GridY,
                Rotation = element.Rotation,
                Name = element.Name,
                Address = element.Address
            });
        }

        project.SignalBoxPlan = plan;
        LogMessage("SAVE", $"Saved {plan.Elements.Count} elements to project");
    }

    private void LoadFromDomainPlan(DomainPlan plan)
    {
        _elements.Clear();
        _elementVisuals.Clear();
        _trackCanvas.Children.Clear();
        DrawGrid();

        foreach (var domainElement in plan.Elements)
        {
            var element = new SignalBoxElement
            {
                Id = domainElement.Id,
                Type = MapFromSymbol(domainElement.Symbol),
                GridX = domainElement.X,
                GridY = domainElement.Y,
                Rotation = domainElement.Rotation,
                Name = domainElement.Name,
                Address = domainElement.Address
            };
            _elements.Add(element);

            var visual = CreateElementVisual(element);
            Canvas.SetLeft(visual, element.GridX * GridCellSize);
            Canvas.SetTop(visual, element.GridY * GridCellSize);
            _trackCanvas.Children.Add(visual);
            _elementVisuals[element.Id] = visual;
        }
    }

    private static DomainSymbol MapToSymbol(SignalBoxElementType type) => type switch
    {
        SignalBoxElementType.TrackStraight => DomainSymbol.TrackStraight,
        SignalBoxElementType.TrackCurve45 => DomainSymbol.TrackCurve45,
        SignalBoxElementType.TrackCurve90 => DomainSymbol.TrackCurve90,
        SignalBoxElementType.TrackEndStop => DomainSymbol.TrackEnd,
        SignalBoxElementType.SwitchLeft => DomainSymbol.SwitchSimpleLeft,
        SignalBoxElementType.SwitchRight => DomainSymbol.SwitchSimpleRight,
        SignalBoxElementType.SwitchDouble => DomainSymbol.SwitchDoubleSlip,
        SignalBoxElementType.SwitchCrossing => DomainSymbol.SwitchDiamond,
        SignalBoxElementType.SignalMain => DomainSymbol.SignalKsMain,
        SignalBoxElementType.SignalDistant => DomainSymbol.SignalKsDistant,
        SignalBoxElementType.SignalCombined => DomainSymbol.SignalKsCombined,
        SignalBoxElementType.SignalShunting => DomainSymbol.SignalSh,
        SignalBoxElementType.SignalSpeed => DomainSymbol.SignalZs3,
        SignalBoxElementType.Platform => DomainSymbol.Platform,
        SignalBoxElementType.FeedbackPoint => DomainSymbol.Detector,
        SignalBoxElementType.Label => DomainSymbol.Label,
        _ => DomainSymbol.TrackStraight
    };

    private static SignalBoxElementType MapFromSymbol(DomainSymbol symbol) => symbol switch
    {
        DomainSymbol.TrackStraight => SignalBoxElementType.TrackStraight,
        DomainSymbol.TrackCurve45 => SignalBoxElementType.TrackCurve45,
        DomainSymbol.TrackCurve90 => SignalBoxElementType.TrackCurve90,
        DomainSymbol.TrackEnd => SignalBoxElementType.TrackEndStop,
        DomainSymbol.SwitchSimpleLeft => SignalBoxElementType.SwitchLeft,
        DomainSymbol.SwitchSimpleRight => SignalBoxElementType.SwitchRight,
        DomainSymbol.SwitchDoubleSlip => SignalBoxElementType.SwitchDouble,
        DomainSymbol.SwitchDiamond => SignalBoxElementType.SwitchCrossing,
        DomainSymbol.SignalKsMain or DomainSymbol.SignalHvMain => SignalBoxElementType.SignalMain,
        DomainSymbol.SignalKsDistant or DomainSymbol.SignalHvDistant => SignalBoxElementType.SignalDistant,
        DomainSymbol.SignalKsCombined or DomainSymbol.SignalHvCombined => SignalBoxElementType.SignalCombined,
        DomainSymbol.SignalSh or DomainSymbol.SignalDwarf => SignalBoxElementType.SignalShunting,
        DomainSymbol.SignalZs3 => SignalBoxElementType.SignalSpeed,
        DomainSymbol.Platform or DomainSymbol.PlatformEdge => SignalBoxElementType.Platform,
        DomainSymbol.Detector or DomainSymbol.AxleCounter => SignalBoxElementType.FeedbackPoint,
        DomainSymbol.Label or DomainSymbol.TrackNumber => SignalBoxElementType.Label,
        _ => SignalBoxElementType.TrackStraight
    };

    private void Z21_FeedbackReceived(FeedbackResult feedback)
    {
        DispatcherQueue.TryEnqueue(() =>
        {
            UpdateFeedbackPoint(feedback.InPort);
            LogMessage("FEEDBACK", $"InPort {feedback.InPort}: OCCUPIED");
        });
    }

    private Grid BuildLayout()
    {
        var rootGrid = new Grid { Background = new SolidColorBrush(ColorBackground) };
        rootGrid.ColumnDefinitions.Add(new ColumnDefinition { Width = new GridLength(180) }); // Toolbox
        rootGrid.ColumnDefinitions.Add(new ColumnDefinition { Width = GridLength.Auto });     // Divider 1
        rootGrid.ColumnDefinitions.Add(new ColumnDefinition { Width = new GridLength(1, GridUnitType.Star) }); // Canvas
        rootGrid.ColumnDefinitions.Add(new ColumnDefinition { Width = GridLength.Auto });     // Divider 2
        rootGrid.ColumnDefinitions.Add(new ColumnDefinition { Width = new GridLength(220) }); // Properties Panel
        rootGrid.RowDefinitions.Add(new RowDefinition { Height = GridLength.Auto });          // Toolbar
        rootGrid.RowDefinitions.Add(new RowDefinition { Height = new GridLength(1, GridUnitType.Star) }); // Main
        rootGrid.RowDefinitions.Add(new RowDefinition { Height = new GridLength(120) });      // Message Log
        rootGrid.RowDefinitions.Add(new RowDefinition { Height = GridLength.Auto });          // Status Bar

        // Toolbar (spans all columns)
        var toolbar = BuildToolbar();
        Grid.SetRow(toolbar, 0);
        Grid.SetColumnSpan(toolbar, 5);
        rootGrid.Children.Add(toolbar);

        // Toolbox
        var toolboxPanel = BuildToolbox();
        Grid.SetRow(toolboxPanel, 1);
        Grid.SetColumn(toolboxPanel, 0);
        rootGrid.Children.Add(toolboxPanel);

        // Divider 1
        var divider1 = new Border { Width = 2, Background = new SolidColorBrush(Color.FromArgb(255, 60, 80, 100)) };
        Grid.SetRow(divider1, 1);
        Grid.SetColumn(divider1, 1);
        rootGrid.Children.Add(divider1);

        // Track Canvas
        var canvasPanel = BuildCanvasPanel();
        Grid.SetRow(canvasPanel, 1);
        Grid.SetColumn(canvasPanel, 2);
        rootGrid.Children.Add(canvasPanel);

        // Divider 2
        var divider2 = new Border { Width = 2, Background = new SolidColorBrush(Color.FromArgb(255, 60, 80, 100)) };
        Grid.SetRow(divider2, 1);
        Grid.SetColumn(divider2, 3);
        rootGrid.Children.Add(divider2);

        // Properties Panel
        var propertiesPanel = BuildPropertiesPanel();
        Grid.SetRow(propertiesPanel, 1);
        Grid.SetColumn(propertiesPanel, 4);
        rootGrid.Children.Add(propertiesPanel);

        // Message Log (spans all columns)
        var messagePanel = BuildMessagePanel();
        Grid.SetRow(messagePanel, 2);
        Grid.SetColumnSpan(messagePanel, 5);
        rootGrid.Children.Add(messagePanel);

        // Status Bar (spans all columns)
        var statusBar = BuildStatusBar();
        Grid.SetRow(statusBar, 3);
        Grid.SetColumnSpan(statusBar, 5);
        rootGrid.Children.Add(statusBar);

        return rootGrid;
    }

    private Border BuildToolbar()
    {
        var panel = new StackPanel { Orientation = Orientation.Horizontal, Spacing = 8, Padding = new Thickness(12, 8, 12, 8) };

        // Title
        var title = new TextBlock
        {
            Text = "MOBAixl - Interlocking System",
            FontSize = 16,
            FontWeight = Microsoft.UI.Text.FontWeights.Bold,
            Foreground = new SolidColorBrush(Colors.White),
            VerticalAlignment = VerticalAlignment.Center
        };
        panel.Children.Add(title);

        return new Border
        {
            Background = new SolidColorBrush(Color.FromArgb(255, 30, 50, 70)),
            BorderBrush = new SolidColorBrush(Color.FromArgb(255, 50, 70, 90)),
            BorderThickness = new Thickness(0, 0, 0, 2),
            Child = panel
        };
    }

    private Border BuildToolbox()
    {
        var scroll = new ScrollViewer { VerticalScrollBarVisibility = ScrollBarVisibility.Auto };
        _toolbox = new StackPanel { Spacing = 12, Padding = new Thickness(12) };

        // Header with stylish design
        var headerPanel = new StackPanel { Spacing = 4, Margin = new Thickness(0, 0, 0, 16) };
        headerPanel.Children.Add(new TextBlock
        {
            Text = "TOOLBOX",
            FontSize = 11,
            FontWeight = Microsoft.UI.Text.FontWeights.Bold,
            Foreground = new SolidColorBrush(Color.FromArgb(180, 255, 255, 255)),
            CharacterSpacing = 100
        });
        headerPanel.Children.Add(new Border
        {
            Height = 2,
            Background = new LinearGradientBrush
            {
                StartPoint = new Point(0, 0),
                EndPoint = new Point(1, 0),
                GradientStops = {
                    new GradientStop { Color = ColorRouteSet, Offset = 0 },
                    new GradientStop { Color = Colors.Transparent, Offset = 1 }
                }
            },
            Margin = new Thickness(0, 4, 0, 0)
        });
        _toolbox.Children.Add(headerPanel);

        // Track Elements - Icon Grid
        _toolbox.Children.Add(CreateToolboxCategory("GLEISE", "\uE909"));
        _toolbox.Children.Add(CreateIconGrid([
            ("Gerade", SignalBoxElementType.TrackStraight, CreateTrackStraightIcon()),
            ("Kurve 45", SignalBoxElementType.TrackCurve45, CreateTrackCurve45Icon()),
            ("Kurve 90", SignalBoxElementType.TrackCurve90, CreateTrackCurve90Icon()),
            ("Prellbock", SignalBoxElementType.TrackEndStop, CreateBufferStopIcon())
        ]));

        // Switch Elements - Icon Grid
        _toolbox.Children.Add(CreateToolboxCategory("WEICHEN", "\uE8AB"));
        _toolbox.Children.Add(CreateIconGrid([
            ("EW li", SignalBoxElementType.SwitchLeft, CreateSwitchLeftIcon()),
            ("EW re", SignalBoxElementType.SwitchRight, CreateSwitchRightIcon()),
            ("DKW", SignalBoxElementType.SwitchDouble, CreateDoubleSlipIcon()),
            ("Kreuz", SignalBoxElementType.SwitchCrossing, CreateCrossingIcon())
        ]));

        // Main Signals (Ks) - Icon Grid
        _toolbox.Children.Add(CreateToolboxCategory("SIGNALE Ks", "\uE8B8"));
        _toolbox.Children.Add(CreateIconGrid([
            ("Ks Hp", SignalBoxElementType.SignalMain, CreateKsSignalIcon()),
            ("Ks Vr", SignalBoxElementType.SignalDistant, CreateDistantSignalIcon()),
            ("Ks Komb", SignalBoxElementType.SignalCombined, CreateKsCombinedIcon()),
            ("Zwergsig", SignalBoxElementType.SignalShunting, CreateDwarfSignalIcon())
        ]));

        // H/V Signals - Icon Grid
        _toolbox.Children.Add(CreateToolboxCategory("SIGNALE H/V", "\uE8B8"));
        _toolbox.Children.Add(CreateIconGrid([
            ("Hp", SignalBoxElementType.SignalMain, CreateMainSignalIcon()),
            ("Vr", SignalBoxElementType.SignalDistant, CreateHvDistantIcon()),
            ("Sh", SignalBoxElementType.SignalShunting, CreateShuntingSignalIcon()),
            ("Zs3", SignalBoxElementType.SignalSpeed, CreateSpeedIndicatorIcon())
        ]));

        // Additional Signals - Icon Grid
        _toolbox.Children.Add(CreateToolboxCategory("ZUSATZ", "\uE946"));
        _toolbox.Children.Add(CreateIconGrid([
            ("Zs1", SignalBoxElementType.SignalSpeed, CreateZs1Icon()),
            ("Zs3v", SignalBoxElementType.SignalSpeed, CreateZs3vIcon()),
            ("Ra12", SignalBoxElementType.SignalShunting, CreateRa12Icon()),
            ("BUEUE", SignalBoxElementType.Platform, CreateLevelCrossingIcon())
        ]));

        // Infrastructure - Icon Grid
        _toolbox.Children.Add(CreateToolboxCategory("INFRASTRUKTUR", "\uE913"));
        _toolbox.Children.Add(CreateIconGrid([
            ("Bhst", SignalBoxElementType.Platform, CreatePlatformIcon()),
            ("RM", SignalBoxElementType.FeedbackPoint, CreateFeedbackIcon()),
            ("Sperre", SignalBoxElementType.Buffer, CreateDerailIcon()),
            ("Label", SignalBoxElementType.Label, CreateLabelIcon())
        ]));

        scroll.Content = _toolbox;

        return new Border
        {
            Background = new SolidColorBrush(Color.FromArgb(255, 20, 35, 50)),
            Child = scroll
        };
    }

    private static Border CreateToolboxCategory(string name, string glyph)
    {
        var panel = new StackPanel { Orientation = Orientation.Horizontal, Spacing = 8 };
        panel.Children.Add(new FontIcon
        {
            Glyph = glyph,
            FontSize = 12,
            Foreground = new SolidColorBrush(ColorSignalYellow)
        });
        panel.Children.Add(new TextBlock
        {
            Text = name,
            FontSize = 11,
            FontWeight = Microsoft.UI.Text.FontWeights.SemiBold,
            Foreground = new SolidColorBrush(ColorSignalYellow),
            CharacterSpacing = 50
        });

        return new Border
        {
            Padding = new Thickness(0, 8, 0, 4),
            Child = panel
        };
    }

    private Grid CreateIconGrid((string tooltip, SignalBoxElementType type, UIElement icon)[] items)
    {
        var grid = new Grid { ColumnSpacing = 8, RowSpacing = 8 };
        grid.ColumnDefinitions.Add(new ColumnDefinition { Width = new GridLength(1, GridUnitType.Star) });
        grid.ColumnDefinitions.Add(new ColumnDefinition { Width = new GridLength(1, GridUnitType.Star) });

        int row = 0;
        for (int i = 0; i < items.Length; i++)
        {
            if (i % 2 == 0)
            {
                grid.RowDefinitions.Add(new RowDefinition { Height = GridLength.Auto });
            }

            var (tooltip, type, icon) = items[i];
            var button = CreateToolboxIconButton(tooltip, type, icon);
            Grid.SetRow(button, row);
            Grid.SetColumn(button, i % 2);
            grid.Children.Add(button);

            if (i % 2 == 1) row++;
        }

        return grid;
    }

    private Border CreateToolboxIconButton(string tooltip, SignalBoxElementType type, UIElement icon)
    {
        var button = new Border
        {
            Width = 56,
            Height = 56,
            CornerRadius = new CornerRadius(8),
            Background = new SolidColorBrush(Color.FromArgb(255, 35, 55, 75)),
            BorderBrush = new SolidColorBrush(Color.FromArgb(80, 100, 150, 200)),
            BorderThickness = new Thickness(1),
            Tag = type,
            CanDrag = true,
            HorizontalAlignment = HorizontalAlignment.Center
        };

        var content = new Grid();
        content.Children.Add(icon);

        // Subtle label at bottom
        var label = new TextBlock
        {
            Text = tooltip,
            FontSize = 9,
            Foreground = new SolidColorBrush(Color.FromArgb(150, 255, 255, 255)),
            HorizontalAlignment = HorizontalAlignment.Center,
            VerticalAlignment = VerticalAlignment.Bottom,
            Margin = new Thickness(0, 0, 0, 4)
        };
        content.Children.Add(label);
        button.Child = content;

        // Tooltip
        ToolTipService.SetToolTip(button, tooltip);

        // Hover effects
        button.PointerEntered += (s, e) =>
        {
            button.Background = new SolidColorBrush(Color.FromArgb(255, 50, 80, 110));
            button.BorderBrush = new SolidColorBrush(ColorRouteSet);
        };
        button.PointerExited += (s, e) =>
        {
            button.Background = new SolidColorBrush(Color.FromArgb(255, 35, 55, 75));
            button.BorderBrush = new SolidColorBrush(Color.FromArgb(80, 100, 150, 200));
        };

        // WinUI Drag & Drop - New element from toolbox
        button.DragStarting += (s, e) =>
        {
            e.Data.SetText($"{DragDataNewElement}{(int)type}");
            e.Data.RequestedOperation = DataPackageOperation.Copy;
            LogMessage("DRAG", $"New element: {type}");
        };

        return button;
    }

