    private Border BuildCanvasPanel()
    {
        var scroll = new ScrollViewer
        {
            HorizontalScrollBarVisibility = ScrollBarVisibility.Auto,
            VerticalScrollBarVisibility = ScrollBarVisibility.Auto,
            ZoomMode = ZoomMode.Enabled,
            MinZoomFactor = 0.5f,
            MaxZoomFactor = 2.0f
        };

        _trackCanvas = new Canvas
        {
            Width = GridColumns * GridCellSize,
            Height = GridRows * GridCellSize,
            Background = new SolidColorBrush(ColorBackground),
            AllowDrop = true
        };

        // WinUI Drag & Drop events
        _trackCanvas.DragOver += OnCanvasDragOver;
        _trackCanvas.Drop += OnCanvasDrop;

        // Mouse wheel for rotation
        _trackCanvas.PointerWheelChanged += OnCanvasPointerWheelChanged;

        DrawGrid();
        scroll.Content = _trackCanvas;

        return new Border { Background = new SolidColorBrush(ColorBackground), Child = scroll };
    }

    private void DrawGrid()
    {
        // Draw grid lines
        for (int x = 0; x <= GridColumns; x++)
        {
            var line = new Line
            {
                X1 = x * GridCellSize, Y1 = 0,
                X2 = x * GridCellSize, Y2 = GridRows * GridCellSize,
                Stroke = new SolidColorBrush(Color.FromArgb(40, 100, 150, 200)),
                StrokeThickness = 1
            };
            _trackCanvas.Children.Add(line);
        }
        for (int y = 0; y <= GridRows; y++)
        {
            var line = new Line
            {
                X1 = 0, Y1 = y * GridCellSize,
                X2 = GridColumns * GridCellSize, Y2 = y * GridCellSize,
                Stroke = new SolidColorBrush(Color.FromArgb(40, 100, 150, 200)),
                StrokeThickness = 1
            };
            _trackCanvas.Children.Add(line);
        }
    }

    private Border BuildMessagePanel()
    {
        var panel = new Grid();
        panel.RowDefinitions.Add(new RowDefinition { Height = GridLength.Auto });
        panel.RowDefinitions.Add(new RowDefinition { Height = new GridLength(1, GridUnitType.Star) });

        var header = new TextBlock
        {
            Text = "MESSAGE LOG",
            FontWeight = Microsoft.UI.Text.FontWeights.Bold,
            Foreground = new SolidColorBrush(Colors.White),
            FontSize = 11,
            Margin = new Thickness(8, 4, 8, 4)
        };
        Grid.SetRow(header, 0);
        panel.Children.Add(header);

        _messageListView = new ListView
        {
            ItemsSource = _messageLog,
            SelectionMode = ListViewSelectionMode.None,
            Background = new SolidColorBrush(Colors.Transparent),
            Padding = new Thickness(8, 0, 8, 0)
        };
        Grid.SetRow(_messageListView, 1);
        panel.Children.Add(_messageListView);

        return new Border
        {
            Background = new SolidColorBrush(Color.FromArgb(255, 15, 30, 45)),
            BorderBrush = new SolidColorBrush(Color.FromArgb(255, 40, 60, 80)),
            BorderThickness = new Thickness(0, 2, 0, 0),
            Child = panel
        };
    }

    private Border BuildStatusBar()
    {
        var panel = new StackPanel { Orientation = Orientation.Horizontal, Padding = new Thickness(8, 4, 8, 4), Spacing = 16 };

        // Connection LED
        var led = new Ellipse { Width = 12, Height = 12, Fill = new SolidColorBrush(_z21.IsConnected ? ColorSignalGreen : ColorSignalRed) };
        panel.Children.Add(led);
        panel.Children.Add(new TextBlock { Text = _z21.IsConnected ? "Z21 Online" : "Z21 Offline", Foreground = new SolidColorBrush(Colors.White), FontSize = 12 });

        panel.Children.Add(new Border { Width = 1, Height = 16, Background = new SolidColorBrush(Color.FromArgb(100, 255, 255, 255)) });

        _statusTextBlock = new TextBlock { Text = "Ready - Drag elements from toolbox", Foreground = new SolidColorBrush(ColorSignalGreen), FontFamily = new FontFamily("Consolas"), FontSize = 12 };
        panel.Children.Add(_statusTextBlock);

        return new Border { Background = new SolidColorBrush(Color.FromArgb(255, 20, 40, 55)), Child = panel };
    }

    private Border BuildPropertiesPanel()
    {
        var scroll = new ScrollViewer { VerticalScrollBarVisibility = ScrollBarVisibility.Auto };
        _propertiesPanel = new StackPanel { Spacing = 12, Padding = new Thickness(12) };

        // Header
        var headerPanel = new StackPanel { Spacing = 4, Margin = new Thickness(0, 0, 0, 16) };
        headerPanel.Children.Add(new TextBlock
        {
            Text = "EIGENSCHAFTEN",
