    private void SelectElement(SignalBoxElement? element)
    {
        // Deselect previous
        if (_selectedVisual != null)
        {
            _selectedVisual.Opacity = 1.0;
        }

        _selectedElement = element;

        // Highlight new selection
        if (element != null && _elementVisuals.TryGetValue(element.Id, out var visual))
        {
            _selectedVisual = visual;
            // Add selection border effect (via opacity pulse or scale)
        }
        else
        {
            _selectedVisual = null;
        }

        UpdatePropertiesPanel();
    }

    private void RefreshSelectedVisual()
    {
        if (_selectedElement == null) return;

        if (_elementVisuals.TryGetValue(_selectedElement.Id, out var visual))
        {
            _trackCanvas.Children.Remove(visual);
            var newVisual = CreateElementVisual(_selectedElement);
            Canvas.SetLeft(newVisual, _selectedElement.GridX * GridCellSize);
            Canvas.SetTop(newVisual, _selectedElement.GridY * GridCellSize);
            _trackCanvas.Children.Add(newVisual);
            _elementVisuals[_selectedElement.Id] = newVisual;
            _selectedVisual = newVisual;
        }
    }

    private void DeleteSelectedElement()
    {
        if (_selectedElement == null) return;

        if (_elementVisuals.TryGetValue(_selectedElement.Id, out var visual))
        {
            _trackCanvas.Children.Remove(visual);
            _elementVisuals.Remove(_selectedElement.Id);
        }

        _elements.Remove(_selectedElement);
        LogMessage("DELETE", $"Removed {_selectedElement.Type}");
        SelectElement(null);
    }

