    private Grid CreateSwitchVisual(SignalBoxElement element)
    {
        var grid = new Grid();
        var color = GetStateColor(element.State);

        // Main track
        grid.Children.Add(new Line { X1 = 5, Y1 = 30, X2 = 55, Y2 = 30, Stroke = new SolidColorBrush(color), StrokeThickness = 6 });

        // Diverging track
        var divergeColor = element.SwitchPosition == SwitchPosition.Diverging ? ColorRouteSet : Color.FromArgb(100, 100, 100, 100);
        grid.Children.Add(new Line
        {
            X1 = 30, Y1 = 30,
            X2 = element.Type == SignalBoxElementType.SwitchLeft ? 50 : 50,
            Y2 = element.Type == SignalBoxElementType.SwitchLeft ? 10 : 50,
            Stroke = new SolidColorBrush(divergeColor),
            StrokeThickness = 4
        });

        // Switch motor indicator
        var motorLed = new Ellipse
        {
            Width = 8, Height = 8,
            Fill = new SolidColorBrush(element.SwitchPosition == SwitchPosition.Straight ? ColorSignalGreen : ColorSignalYellow)
        };
        Canvas.SetLeft(motorLed, 26);
        Canvas.SetTop(motorLed, 26);
        grid.Children.Add(motorLed);

        return grid;
    }

    private Grid CreateSignalVisual(SignalBoxElement element, bool isMain)
    {
        var grid = new Grid();

        // Signal mast
        grid.Children.Add(new Rectangle { Width = 4, Height = 40, Fill = new SolidColorBrush(Colors.DarkGray) });

        // Signal head background
        var headBg = new Rectangle { Width = 20, Height = isMain ? 35 : 25, Fill = new SolidColorBrush(Colors.Black), RadiusX = 3, RadiusY = 3 };
        Canvas.SetLeft(headBg, 20);
        Canvas.SetTop(headBg, 5);
        grid.Children.Add(headBg);

        // LEDs based on aspect
        if (isMain)
        {
            // Red LED (top)
            grid.Children.Add(CreateLed(25, 10, element.SignalAspect == SignalAspect.Hp0 ? ColorSignalRed : Color.FromArgb(50, 255, 0, 0)));
            // Green LED (bottom)
            grid.Children.Add(CreateLed(25, 25, element.SignalAspect == SignalAspect.Hp1 ? ColorSignalGreen : Color.FromArgb(50, 0, 255, 0)));
        }
        else
        {
            // Vorsignal: Two yellow/green LEDs
            grid.Children.Add(CreateLed(23, 10, element.SignalAspect == SignalAspect.Vr0 ? ColorSignalYellow : Color.FromArgb(50, 255, 200, 0)));
            grid.Children.Add(CreateLed(32, 10, element.SignalAspect == SignalAspect.Vr0 ? ColorSignalYellow : Color.FromArgb(50, 255, 200, 0)));
        }

        return grid;
    }

    private Grid CreateKsSignalVisual(SignalBoxElement element)
    {
        var grid = new Grid();

        // Modern Ks signal head
        var headBg = new Rectangle { Width = 24, Height = 45, Fill = new SolidColorBrush(Colors.Black), RadiusX = 4, RadiusY = 4 };
        Canvas.SetLeft(headBg, 18);
        Canvas.SetTop(headBg, 5);
        grid.Children.Add(headBg);

        // Single aspect LED (Ks signals use one multi-color LED concept shown as separate)
        var aspectColor = element.SignalAspect switch
        {
            SignalAspect.Ks1 => ColorSignalGreen,
            SignalAspect.Ks2 => ColorSignalYellow,
            _ => ColorSignalRed
        };
        grid.Children.Add(CreateLed(25, 15, aspectColor, 14));

        // Speed indicator (Zs 3) position
        grid.Children.Add(new TextBlock
        {
            Text = "8",
            FontSize = 10,
            FontWeight = Microsoft.UI.Text.FontWeights.Bold,
            Foreground = new SolidColorBrush(ColorSignalYellow)
        });

        return grid;
    }

    private Ellipse CreateLed(double x, double y, Color color, double size = 10)
    {
        var led = new Ellipse
        {
            Width = size,
            Height = size,
            Fill = new RadialGradientBrush
            {
                GradientStops =
                {
                    new GradientStop { Color = Colors.White, Offset = 0 },
                    new GradientStop { Color = color, Offset = 0.3 },
                    new GradientStop { Color = Color.FromArgb(200, color.R, color.G, color.B), Offset = 1 }
                }
            }
        };
        Canvas.SetLeft(led, x);
        Canvas.SetTop(led, y);
        return led;
    }

