    private static Color GetStateColor(SignalBoxElementState state)
    {
        return state switch
        {
            SignalBoxElementState.Free => ColorTrackFree,
            SignalBoxElementState.Occupied => ColorTrackOccupied,
            SignalBoxElementState.RouteSet => ColorRouteSet,
            SignalBoxElementState.RouteClearing => ColorRouteClearing,
            SignalBoxElementState.Blocked => ColorBlocked,
            _ => ColorTrackFree
        };
    }

    private void ToggleElementState(SignalBoxElement element)
    {
        // Toggle state on click
        element.State = element.State switch
        {
            SignalBoxElementState.Free => SignalBoxElementState.RouteSet,
            SignalBoxElementState.RouteSet => SignalBoxElementState.Occupied,
            SignalBoxElementState.Occupied => SignalBoxElementState.Free,
            _ => SignalBoxElementState.Free
        };

        // Toggle signal/switch
        if (element.Type is SignalBoxElementType.SignalMain or SignalBoxElementType.SignalCombined)
        {
            element.SignalAspect = element.SignalAspect == SignalAspect.Hp0 ? SignalAspect.Hp1 : SignalAspect.Hp0;
        }
        else if (element.Type is SignalBoxElementType.SwitchLeft or SignalBoxElementType.SwitchRight)
        {
            element.SwitchPosition = element.SwitchPosition == SwitchPosition.Straight ? SwitchPosition.Diverging : SwitchPosition.Straight;
        }

        // Refresh visual
        if (_elementVisuals.TryGetValue(element.Id, out var visual))
        {
            _trackCanvas.Children.Remove(visual);
            var newVisual = CreateElementVisual(element);
            Canvas.SetLeft(newVisual, element.GridX * GridCellSize);
            Canvas.SetTop(newVisual, element.GridY * GridCellSize);
            _trackCanvas.Children.Add(newVisual);
            _elementVisuals[element.Id] = newVisual;
        }

        LogMessage("TOGGLE", $"{element.Type} -> {element.State}");
    }

    private void UpdateFeedbackPoint(int address)
    {
        var element = _elements.Find(e => e.Type == SignalBoxElementType.FeedbackPoint && e.Address == address);
        if (element != null)
        {
            element.State = SignalBoxElementState.Occupied;
            ToggleElementState(element); // Refresh visual
        }
    }

    private void CreateDemoLayout()
    {
        // Create demo elements
        AddElement(SignalBoxElementType.SignalMain, 2, 5, 0, SignalAspect.Hp0);
        AddElement(SignalBoxElementType.TrackStraight, 3, 5, 0);
        AddElement(SignalBoxElementType.TrackStraight, 4, 5, 0);
        AddElement(SignalBoxElementType.SwitchRight, 5, 5, 0);
        AddElement(SignalBoxElementType.TrackStraight, 6, 5, 0);
        AddElement(SignalBoxElementType.TrackStraight, 7, 5, 0);
        AddElement(SignalBoxElementType.SignalMain, 8, 5, 180, SignalAspect.Hp1);

        // Branch
        AddElement(SignalBoxElementType.TrackStraight, 6, 4, 45);
        AddElement(SignalBoxElementType.TrackStraight, 7, 3, 0);
        AddElement(SignalBoxElementType.FeedbackPoint, 8, 3, 0);
    }

    private void AddElement(SignalBoxElementType type, int x, int y, int rotation, SignalAspect aspect = SignalAspect.Hp0)
    {
        var element = new SignalBoxElement
        {
            Type = type,
            GridX = x,
            GridY = y,
            Rotation = rotation,
            SignalAspect = aspect,
            Address = type == SignalBoxElementType.FeedbackPoint ? x + y * 10 : 0
        };
        _elements.Add(element);

        var visual = CreateElementVisual(element);
        Canvas.SetLeft(visual, x * GridCellSize);
        Canvas.SetTop(visual, y * GridCellSize);
        _trackCanvas.Children.Add(visual);
        _elementVisuals[element.Id] = visual;
    }

    private void LogMessage(string category, string message)
    {
        var entry = $"[{DateTime.Now:HH:mm:ss}] [{category}] {message}";
        _messageLog.Insert(0, entry);
        while (_messageLog.Count > 50) _messageLog.RemoveAt(_messageLog.Count - 1);
        if (_statusTextBlock != null) _statusTextBlock.Text = message;
    }

